                                // thống kê
                                case 'statistical':
                                    return 'Thống kê';
                                case 'statisticalCatalog':
                                    return 'Thống kê';
                                case 'statisticalProduct':
                                    return 'Thống kê';
                                   
                                // khách hàng 4 trang
                                case 'showUser':
                                    return 'Khách hàng';
                                case 'userVip':
                                    return 'khách hàng';
                                case 'userBlack':
                                    return 'khách hàng';
                                case 'customerOrder':
                                    return 'khách hàng';

                                 // sản phẩm  7 trang  
                                case 'addProduct':
                                    return 'Sản phẩm';
                                case 'addDetailProduct': 
